package com.example.springboot_grupparbete.RabbitMQ;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class EventProducerConfiguration { @Bean public Exchange eventExchange() {   return new TopicExchange("eventExchange"); } @Bean public CustomerService customerService(RabbitTemplate rabbitTemplate, Exchange eventExchange) {   return new CustomerService(rabbitTemplate, senderTopicExchange); }}